mapper.select:
SELECT deptId, deptNo, deptName, sd.levelId, sdl.levelName, sd.areaId, sa.areaName, sd.pId, address, contact, contactNumber, principal, principalNumber, sd.createDate, prodctType FROM sys_dept sd LEFT JOIN sys_dept_level sdl ON sd.levelId = sdl.levelId LEFT JOIN sys_area sa ON sd.areaId = sa.areaId
sd.areaId = #{areaId}
AND sd.pId = #{pId}
ORDER BY level LIMIT #{startNo},#{pageSize}
