mapper.update:
UPDATE detect_report
SET sampleId = #{sampleId}
WHERE detectId = #{detectId}

mapper.select:
SELECT d.detectId, sc.companyName, po.objectName, d.sampleId, di.itemName, dr.reagentName, pt.typeName AS productTypeName, ds.sourceName, dre.resultName, su.userName AS cUserName, d.reviewFlag, d.detectDate FROM detect_report d LEFT JOIN product_object po ON d.objectId = po.objectId LEFT JOIN sys_company sc ON d.companyId = sc.companyId LEFT JOIN sys_user su ON d.detectUserId = su.userId LEFT JOIN detect_item di ON d.itemId = di.itemId LEFT JOIN detect_reagent dr ON d.reagentId = dr.reagentId LEFT JOIN product_type pt ON d.productTypeId = pt.typeId LEFT JOIN detect_source ds ON d.sourceId = ds.sourceId
LEFT JOIN detect_review d2 ON d.detectId = d2.detectId LEFT JOIN detect_result dre ON d2.reviewResultId = dre.resultId
LEFT JOIN detect_result dre ON d.resultId = dre.resultId
sc.areaId = #{areaId}
p.companyId = #{companyId}
AND objectId = #{objectId}
AND itemId = #{itemId}
AND resultId = #{resultId}
ORDER BY createDate LIMIT #{startNo},#{pageSize}

SELECT d.detectId, sc.name AS companyName, po.objectName, d.sampleId, di.itemName, dr.reagentName, pt.name AS productType, ds.sourceName, dre.resultName, su.name AS cUserName, d.reviewFlag, d.detectDate FROM detect_report d LEFT JOIN product_object po ON d.objectId = po.objectId LEFT JOIN sys_company sc ON d.companyId = sc.companyId LEFT JOIN sys_user su ON d.detectUserId = su.userId LEFT JOIN detect_item di ON d.itemId = di.itemId LEFT JOIN detect_reagent dr ON d.reagentId = dr.reagentId LEFT JOIN product_type pt ON d.productTypeId = pt.productTypeId LEFT JOIN detect_source ds ON d.sourceId = ds.sourceId
LEFT JOIN detect_review dd ON d.detectId = dd.detectId LEFT JOIN detect_result dre ON dd.reviewResultId = dre.resultId
LEFT JOIN detect_result dre ON d.resultId = dre.resultId
WHERE d.sampleId = #{sampleId}
AND d.itemId = #{itemId}
AND d.resultId = #{resultId}
ORDER BY createDate LIMIT #{startNo},#{pageSize}
