mapper.update:
UPDATE productive_standard
SET name = #{name}
WHERE standardId = #{standardId}

mapper.select:
SELECT ps.standardId, ps.name AS standardName, ps.createDate, po.objectName, pt.typeName, sc.companyName, su.userName FROM productive_standard ps LEFT JOIN product_object po ON ps.objectId = po.objectId LEFT JOIN product_type pt ON po.productType = pt.typeId LEFT JOIN sys_company sc ON ps.companyId = sc.companyId LEFT JOIN sys_user su ON ps.cUserId = su.userId
ps.companyId = #{companyId}
ps.standardName LIKE '%#{standardName}%'
AND objectId = #{objectId}
AND po.productType = #{productType}
ORDER BY createDate LIMIT #{startNo},#{pageSize}

SELECT
count(*)
FROM productive_standard ps
LEFT JOIN product_object po ON ps.objectId = po.objectId


        ps.companyId = #{companyId}


        ps.standardName LIKE '%#{standardName}%'


        AND objectId = #{objectId}


        AND po.productType = #{productType}

SELECT count(*)
FROM productive_standard
WHERE companyId = #{companyId} AND name = #{name} AND objectId = #{objectId}
